mapper.select:
SELECT SUM(quantity) as quantity 
From inventories 
WHERE 
members_id = #{membersId} 
AND 
itmem_id #{itemsID}

mapper.update:
UPDATE inventroies
	SET 
		price = #{price},
		quantity = #{quantity}
	WHERE
		items_id = #{itemsId}
	AND
		members_id = #{membersId}

mapper.delete:
DELETE FROM inventories
	WHERE
		items_id = #{itemsId}
			AND
		members_id = #{memberesId}
